import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { X } from "lucide-react";

interface AddCandidateModalProps {
  candidate?: any;
  onClose: () => void;
}

export default function AddCandidateModal({ candidate, onClose }: AddCandidateModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isEditing = !!candidate;

  const [formData, setFormData] = useState({
    candidateName: candidate?.candidateName || "",
    email: candidate?.email || "",
    jobId: candidate?.jobId?.toString() || "",
    candidateExperience: candidate?.candidateExperience || "",
    status: candidate?.status || "applied",
    resumeUrl: candidate?.resumeUrl || "",
    candidateSkills: candidate?.candidateSkills || [],
    matchPercentage: candidate?.matchPercentage || "",
    reportLink: candidate?.reportLink || "",
    interviewLink: candidate?.interviewLink || "",
  });

  const [newSkill, setNewSkill] = useState("");

  const { data: jobs, isLoading: jobsLoading } = useQuery({
    queryKey: ["/api/jobs"],
    retry: false,
  });

  const createCandidateMutation = useMutation({
    mutationFn: async (candidateData: any) => {
      const response = await apiRequest("POST", "/api/candidates", candidateData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/candidates"] });
      toast({
        title: "Success",
        description: "Candidate added successfully",
      });
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add candidate",
        variant: "destructive",
      });
    },
  });

  const updateCandidateMutation = useMutation({
    mutationFn: async (candidateData: any) => {
      const response = await apiRequest("PUT", `/api/candidates/${candidate.id}`, candidateData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/candidates"] });
      toast({
        title: "Success",
        description: "Candidate updated successfully",
      });
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update candidate",
        variant: "destructive",
      });
    },
  });

  const handleInputChange = (field: string, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const addSkill = () => {
    if (newSkill.trim() && !formData.candidateSkills.includes(newSkill.trim())) {
      setFormData(prev => ({
        ...prev,
        candidateSkills: [...prev.candidateSkills, newSkill.trim()]
      }));
      setNewSkill("");
    }
  };

  const removeSkill = (skillToRemove: string) => {
    setFormData(prev => ({
      ...prev,
      candidateSkills: prev.candidateSkills.filter(skill => skill !== skillToRemove)
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.candidateName.trim()) {
      toast({
        title: "Validation Error",
        description: "Candidate name is required",
        variant: "destructive",
      });
      return;
    }

    if (!formData.email.trim()) {
      toast({
        title: "Validation Error",
        description: "Email is required",
        variant: "destructive",
      });
      return;
    }

    if (!formData.jobId) {
      toast({
        title: "Validation Error",
        description: "Job position is required",
        variant: "destructive",
      });
      return;
    }

    const submitData = {
      ...formData,
      jobId: parseInt(formData.jobId),
      matchPercentage: formData.matchPercentage ? parseFloat(formData.matchPercentage.toString()) : null,
    };

    if (isEditing) {
      updateCandidateMutation.mutate(submitData);
    } else {
      createCandidateMutation.mutate(submitData);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addSkill();
    }
  };

  const getJobTitle = (jobId: string) => {
    const job = (jobs as any[])?.find((j: any) => j.id.toString() === jobId);
    return job?.jobTitle || "";
  };

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[700px] max-h-[90vh] overflow-y-auto" data-testid="add-candidate-modal">
        <DialogHeader>
          <DialogTitle data-testid="modal-title">
            {isEditing ? "Edit Candidate" : "Add New Candidate"}
          </DialogTitle>
          <DialogDescription>
            {isEditing ? "Update the candidate details below." : "Fill in the details to add a new candidate."}
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Basic Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="candidateName">Full Name *</Label>
              <Input
                id="candidateName"
                value={formData.candidateName}
                onChange={(e) => handleInputChange("candidateName", e.target.value)}
                placeholder="Enter candidate's full name"
                required
                data-testid="candidate-name-input"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="email">Email Address *</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange("email", e.target.value)}
                placeholder="candidate@example.com"
                required
                data-testid="email-input"
              />
            </div>
          </div>

          {/* Job Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="jobId">Job Position *</Label>
              <Select value={formData.jobId} onValueChange={(value) => handleInputChange("jobId", value)}>
                <SelectTrigger data-testid="job-select">
                  <SelectValue placeholder="Select a job position" />
                </SelectTrigger>
                <SelectContent>
                  {jobsLoading ? (
                    <SelectItem value="loading" disabled>Loading jobs...</SelectItem>
                  ) : (jobs as any[])?.length === 0 ? (
                    <SelectItem value="none" disabled>No jobs available</SelectItem>
                  ) : (
                    (jobs as any[])?.map((job: any) => (
                      <SelectItem key={job.id} value={job.id.toString()}>
                        {job.jobTitle}
                      </SelectItem>
                    ))
                  )}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="candidateExperience">Experience</Label>
              <Input
                id="candidateExperience"
                value={formData.candidateExperience}
                onChange={(e) => handleInputChange("candidateExperience", e.target.value)}
                placeholder="e.g., 5 years"
                data-testid="experience-input"
              />
            </div>
          </div>

          {/* Skills */}
          <div className="space-y-2">
            <Label>Skills</Label>
            <div className="flex space-x-2">
              <Input
                value={newSkill}
                onChange={(e) => setNewSkill(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Add a skill and press Enter"
                data-testid="skills-input"
              />
              <Button type="button" onClick={addSkill} data-testid="add-skill-button">
                Add
              </Button>
            </div>
            {formData.candidateSkills.length > 0 && (
              <div className="flex flex-wrap gap-2 mt-2">
                {formData.candidateSkills.map((skill: string, index: number) => (
                  <Badge key={index} variant="secondary" className="flex items-center gap-1">
                    {skill}
                    <X
                      className="h-3 w-3 cursor-pointer"
                      onClick={() => removeSkill(skill)}
                      data-testid={`remove-skill-${index}`}
                    />
                  </Badge>
                ))}
              </div>
            )}
          </div>

          {/* Status and Matching */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="space-y-2">
              <Label htmlFor="status">Status</Label>
              <Select value={formData.status} onValueChange={(value) => handleInputChange("status", value)}>
                <SelectTrigger data-testid="status-select">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="applied">Applied</SelectItem>
                  <SelectItem value="resume_reviewed">Resume Reviewed</SelectItem>
                  <SelectItem value="interview_scheduled">Interview Scheduled</SelectItem>
                  <SelectItem value="technical_round">Technical Round</SelectItem>
                  <SelectItem value="final_round">Final Round</SelectItem>
                  <SelectItem value="hired">Hired</SelectItem>
                  <SelectItem value="rejected">Rejected</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="matchPercentage">Match Percentage</Label>
              <Input
                id="matchPercentage"
                type="number"
                min="0"
                max="100"
                value={formData.matchPercentage}
                onChange={(e) => handleInputChange("matchPercentage", e.target.value)}
                placeholder="e.g., 85"
                data-testid="match-percentage-input"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="resumeUrl">Resume URL</Label>
              <Input
                id="resumeUrl"
                type="url"
                value={formData.resumeUrl}
                onChange={(e) => handleInputChange("resumeUrl", e.target.value)}
                placeholder="https://example.com/resume.pdf"
                data-testid="resume-url-input"
              />
            </div>
          </div>

          {/* Links */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="reportLink">Report Link</Label>
              <Input
                id="reportLink"
                type="url"
                value={formData.reportLink}
                onChange={(e) => handleInputChange("reportLink", e.target.value)}
                placeholder="https://example.com/report.pdf"
                data-testid="report-link-input"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="interviewLink">Interview Link</Label>
              <Input
                id="interviewLink"
                type="url"
                value={formData.interviewLink}
                onChange={(e) => handleInputChange("interviewLink", e.target.value)}
                placeholder="https://meet.google.com/xyz"
                data-testid="interview-link-input"
              />
            </div>
          </div>

          <DialogFooter className="flex space-x-2">
            <Button type="button" variant="outline" onClick={onClose} data-testid="cancel-button">
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={createCandidateMutation.isPending || updateCandidateMutation.isPending}
              data-testid="submit-button"
            >
              {isEditing ? "Update Candidate" : "Add Candidate"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/AddJobModal.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { X } from "lucide-react";

interface AddJobModalProps {
  job?: any;
  onClose: () => void;
}

export default function AddJobModal({ job, onClose }: AddJobModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isEditing = !!job;

  const [formData, setFormData] = useState({
    jobTitle: job?.jobTitle || "",
    jobDescription: job?.jobDescription || "",
    experience: job?.experience || "",
    positionsCount: job?.positionsCount || 1,
    jobStatus: job?.jobStatus || "active",
    note: job?.note || "",
    skills: job?.skills || [],
  });

  const [newSkill, setNewSkill] = useState("");

  const createJobMutation = useMutation({
    mutationFn: async (jobData: any) => {
      const response = await apiRequest("POST", "/api/jobs", jobData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      toast({
        title: "Success",
        description: "Job created successfully",
      });
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create job",
        variant: "destructive",
      });
    },
  });

  const updateJobMutation = useMutation({
    mutationFn: async (jobData: any) => {
      const response = await apiRequest("PUT", `/api/jobs/${job.id}`, jobData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      toast({
        title: "Success",
        description: "Job updated successfully",
      });
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update job",
        variant: "destructive",
      });
    },
  });

  const handleInputChange = (field: string, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const addSkill = () => {
    if (newSkill.trim() && !formData.skills.includes(newSkill.trim())) {
      setFormData(prev => ({
        ...prev,
        skills: [...prev.skills, newSkill.trim()]
      }));
      setNewSkill("");
    }
  };

  const removeSkill = (skillToRemove: string) => {
    setFormData(prev => ({
      ...prev,
      skills: prev.skills.filter(skill => skill !== skillToRemove)
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.jobTitle.trim()) {
      toast({
        title: "Validation Error",
        description: "Job title is required",
        variant: "destructive",
      });
      return;
    }

    const submitData = {
      ...formData,
      positionsCount: parseInt(formData.positionsCount.toString()) || 1,
    };

    if (isEditing) {
      updateJobMutation.mutate(submitData);
    } else {
      createJobMutation.mutate(submitData);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addSkill();
    }
  };

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[600px]" data-testid="add-job-modal">
        <DialogHeader>
          <DialogTitle data-testid="modal-title">
            {isEditing ? "Edit Job" : "Add New Job"}
          </DialogTitle>
          <DialogDescription>
            {isEditing ? "Update the job details below." : "Fill in the details to create a new job posting."}
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="jobTitle">Job Title *</Label>
              <Input
                id="jobTitle"
                value={formData.jobTitle}
                onChange={(e) => handleInputChange("jobTitle", e.target.value)}
                placeholder="e.g., Senior Frontend Developer"
                required
                data-testid="job-title-input"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="experience">Experience Required</Label>
              <Input
                id="experience"
                value={formData.experience}
                onChange={(e) => handleInputChange("experience", e.target.value)}
                placeholder="e.g., 3-5 years"
                data-testid="experience-input"
              />
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="jobDescription">Job Description</Label>
            <Textarea
              id="jobDescription"
              value={formData.jobDescription}
              onChange={(e) => handleInputChange("jobDescription", e.target.value)}
              placeholder="Describe the role, responsibilities, and requirements..."
              rows={4}
              data-testid="job-description-input"
            />
          </div>

          <div className="space-y-2">
            <Label>Skills Required</Label>
            <div className="flex space-x-2">
              <Input
                value={newSkill}
                onChange={(e) => setNewSkill(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Add a skill and press Enter"
                data-testid="skills-input"
              />
              <Button type="button" onClick={addSkill} data-testid="add-skill-button">
                Add
              </Button>
            </div>
            {formData.skills.length > 0 && (
              <div className="flex flex-wrap gap-2 mt-2">
                {formData.skills.map((skill: string, index: number) => (
                  <Badge key={index} variant="secondary" className="flex items-center gap-1">
                    {skill}
                    <X
                      className="h-3 w-3 cursor-pointer"
                      onClick={() => removeSkill(skill)}
                      data-testid={`remove-skill-${index}`}
                    />
                  </Badge>
                ))}
              </div>
            )}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="positionsCount">Number of Positions</Label>
              <Input
                id="positionsCount"
                type="number"
                min="1"
                value={formData.positionsCount}
                onChange={(e) => handleInputChange("positionsCount", parseInt(e.target.value) || 1)}
                data-testid="positions-count-input"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="jobStatus">Status</Label>
              <Select value={formData.jobStatus} onValueChange={(value) => handleInputChange("jobStatus", value)}>
                <SelectTrigger data-testid="job-status-select">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="draft">Draft</SelectItem>
                  <SelectItem value="closed">Closed</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="note">Additional Notes</Label>
            <Textarea
              id="note"
              value={formData.note}
              onChange={(e) => handleInputChange("note", e.target.value)}
              placeholder="Any additional information about this role..."
              rows={2}
              data-testid="note-input"
            />
          </div>

          <DialogFooter className="flex space-x-2">
            <Button type="button" variant="outline" onClick={onClose} data-testid="cancel-button">
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={createJobMutation.isPending || updateJobMutation.isPending}
              data-testid="submit-button"
            >
              {isEditing ? "Update Job" : "Create Job"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Layout.tsx.content:
import { useAuth } from "@/hooks/useAuth";
import { useTheme } from "@/components/ThemeProvider";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";
import { Bell, CheckSquare, Moon, Sun, Brain } from "lucide-react";
import { Link, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import Notifications from "@/pages/Notifications";

interface LayoutProps {
  children: React.ReactNode;
}

export default function Layout({ children }: LayoutProps) {
  const { isAuthenticated, user } = useAuth();
  const { theme, setTheme } = useTheme();
  const [location] = useLocation();
  const [showNotifications, setShowNotifications] = useState(false);

  const { data: notifications = [] } = useQuery<any[]>({
    queryKey: ["/api/notifications"],
    enabled: isAuthenticated,
  });

  const unreadCount = notifications?.filter((n: any) => !n.readStatus).length || 0;

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const isActive = (path: string) => location === path;

  return (
    <div className="min-h-screen bg-background">
      {/* Navigation Bar */}
      <nav className="fixed top-0 left-0 right-0 bg-card shadow-sm z-50 border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <Link href={isAuthenticated ? "/hr/dashboard" : "/"}>
              <div className="flex items-center space-x-3 cursor-pointer" data-testid="nav-logo">
                <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
                  <Brain className="text-primary-foreground" size={20} />
                </div>
                <span className="text-xl font-bold text-foreground">Smart Hiring</span>
              </div>
            </Link>

            {/* Navigation Items */}
            {isAuthenticated && (
              <div className="hidden md:flex items-center space-x-8">
                <Link href="/hr/jobs" data-testid="nav-jobs">
                  <span
                    className={`text-sm font-medium transition-colors hover:text-primary cursor-pointer ${
                      isActive("/hr/jobs") ? "text-primary" : "text-muted-foreground"
                    }`}
                  >
                    Jobs
                  </span>
                </Link>
                <Link href="/hr/candidates" data-testid="nav-candidates">
                  <span
                    className={`text-sm font-medium transition-colors hover:text-primary cursor-pointer ${
                      isActive("/hr/candidates") ? "text-primary" : "text-muted-foreground"
                    }`}
                  >
                    Candidates
                  </span>
                </Link>
                <Link href="/hr/upload" data-testid="nav-upload">
                  <span
                    className={`text-sm font-medium transition-colors hover:text-primary cursor-pointer ${
                      isActive("/hr/upload") ? "text-primary" : "text-muted-foreground"
                    }`}
                  >
                    Upload & Add
                  </span>
                </Link>
              </div>
            )}

            {/* Right Side Items */}
            <div className="flex items-center space-x-4">
              {/* Theme Toggle */}
              <Button
                variant="ghost"
                size="icon"
                onClick={toggleTheme}
                data-testid="theme-toggle"
              >
                {theme === "light" ? (
                  <Moon className="h-5 w-5" />
                ) : (
                  <Sun className="h-5 w-5" />
                )}
              </Button>

              {isAuthenticated ? (
                <>
                  {/* Notifications */}
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => setShowNotifications(true)}
                    className="relative"
                    data-testid="notifications-button"
                  >
                    <Bell className="h-5 w-5" />
                    {unreadCount > 0 && (
                      <Badge
                        variant="destructive"
                        className="absolute -top-1 -right-1 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs"
                      >
                        {unreadCount}
                      </Badge>
                    )}
                  </Button>

                  {/* User Profile */}
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" className="relative h-8 w-8 rounded-full" data-testid="user-menu">
                        <Avatar className="h-8 w-8">
                          <AvatarImage src={user?.profileImageUrl || undefined} alt={user?.name || ""} />
                          <AvatarFallback>
                            {user?.name?.charAt(0) || user?.email?.charAt(0) || "U"}
                          </AvatarFallback>
                        </Avatar>
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent className="w-56" align="end" forceMount>
                      <DropdownMenuItem asChild>
                        <Link href="/hr/profile">
                          <span className="w-full cursor-pointer" data-testid="profile-link">Profile</span>
                        </Link>
                      </DropdownMenuItem>
                      <DropdownMenuItem asChild>
                        <Link href="/hr/notifications">
                          <span className="w-full cursor-pointer" data-testid="notifications-link">Notifications</span>
                        </Link>
                      </DropdownMenuItem>
                      <DropdownMenuItem 
                        onClick={async () => {
                          try {
                            await fetch('/api/auth/logout', { method: 'POST' });
                            window.location.href = '/login';
                          } catch (error) {
                            console.error('Logout failed:', error);
                          }
                        }}
                        data-testid="logout-button"
                      >
                        Logout
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </>
              ) : (
                <div className="flex items-center space-x-3">
                  <Button variant="ghost" asChild data-testid="login-button">
                    <Link href="/login">
                      <span>Login</span>
                    </Link>
                  </Button>
                  <Button asChild data-testid="signup-button">
                    <Link href="/signup">
                      <span>Sign Up</span>
                    </Link>
                  </Button>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="pt-16">
        {children}
      </div>

      {/* Notifications Modal */}
      {showNotifications && (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4">
          <div className="bg-card rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
            <Notifications onClose={() => setShowNotifications(false)} />
          </div>
        </div>
      )}
    </div>
  );
}

