import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Progress } from "@/components/ui/progress";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Plus, Search, UserCheck, UserX, Upload, Edit, Trash2 } from "lucide-react";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import AddCandidateModal from "@/components/AddCandidateModal";

export default function Candidates() {
  const { isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [positionFilter, setPositionFilter] = useState("all");
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingCandidate, setEditingCandidate] = useState(null);
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [selectedCandidate, setSelectedCandidate] = useState<any>(null);
  const [editFormData, setEditFormData] = useState({
    status: "",
    interviewLink: "",
    technicalPersonEmail: ""
  });

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: candidates = [], isLoading: candidatesLoading } = useQuery<any[]>({
    queryKey: ["/api/candidates"],
    retry: false,
  });

  const { data: jobs = [] } = useQuery<any[]>({
    queryKey: ["/api/jobs"],
    retry: false,
  });

  const updateCandidateMutation = useMutation({
    mutationFn: async ({ id, status, interviewLink, technicalPersonEmail }: { id: number; status: string; interviewLink?: string; technicalPersonEmail?: string }) => {
      await apiRequest("PUT", `/api/candidates/${id}`, { status, interviewLink, technicalPersonEmail });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/candidates"] });
      toast({
        title: "Success",
        description: "Candidate status updated successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update candidate status",
        variant: "destructive",
      });
    },
  });

  const handleStatusUpdate = (candidateId: number, newStatus: string) => {
    updateCandidateMutation.mutate({ id: candidateId, status: newStatus });
  };



  const handleCloseModal = () => {
    setShowAddModal(false);
    setEditingCandidate(null);
  };

  const handleEditCandidate = (candidate: any) => {
    setSelectedCandidate(candidate);
    setEditFormData({
      status: candidate.status || "",
      interviewLink: "",
      technicalPersonEmail: ""
    });
    setShowEditDialog(true);
  };

  const handleDeleteCandidate = (candidate: any) => {
    setSelectedCandidate(candidate);
    setShowDeleteDialog(true);
  };

  const handleSaveEdit = () => {
    if (selectedCandidate) {
      updateCandidateMutation.mutate({ 
        id: selectedCandidate.id, 
        status: editFormData.status,
        interviewLink: editFormData.interviewLink,
        technicalPersonEmail: editFormData.technicalPersonEmail
      });
      setShowEditDialog(false);
      setSelectedCandidate(null);
    }
  };

  const deleteCandidateMutation = useMutation({
    mutationFn: async (candidateId: number) => {
      await apiRequest(`/api/candidates/${candidateId}`, { method: "DELETE" });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/candidates"] });
      setShowDeleteDialog(false);
      setSelectedCandidate(null);
      toast({
        title: "Success",
        description: "Candidate deleted successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete candidate",
        variant: "destructive",
      });
    },
  });

  const handleConfirmDelete = () => {
    if (selectedCandidate) {
      deleteCandidateMutation.mutate(selectedCandidate.id);
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'resume_reviewed':
        return <Badge className="bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">Resume Reviewed</Badge>;
      case 'interview_scheduled':
        return <Badge className="bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200">Interview Scheduled</Badge>;
      case 'report_generated':
        return <Badge className="bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200">Report Generated</Badge>;
      case 'hired':
        return <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">Hired</Badge>;
      case 'not_selected':
        return <Badge className="bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">Not Selected</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getJobTitle = (jobId: number) => {
    const job = jobs?.find((j: any) => j.id === jobId);
    return job?.jobTitle || 'Unknown Position';
  };

  const filteredCandidates = candidates?.filter((candidate: any) => {
    const matchesSearch = candidate.candidateName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         candidate.email?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || candidate.status === statusFilter;
    const matchesPosition = positionFilter === "all" || candidate.jobId?.toString() === positionFilter;

    return matchesSearch && matchesStatus && matchesPosition;
  }) || [];

  if (isLoading || !isAuthenticated) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header with enhanced styling */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-foreground flex items-center gap-3" data-testid="page-title">
              Candidates ({filteredCandidates.length})
            </h1>
            <p className="text-muted-foreground mt-1">Manage and review candidate applications</p>
          </div>
          <div className="flex items-center gap-3">
            <Button asChild data-testid="upload-add-button">
              <a href="/hr/upload">
                <Plus className="mr-2 h-4 w-4" />
                Upload and Add
              </a>
            </Button>
          </div>
        </div>

        {/* Table Card */}
        <Card>
          {/* Table Controls */}
          <CardHeader className="border-b">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search candidates..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 max-w-sm"
                  data-testid="search-input"
                />
              </div>

              {/* Filters */}
              <div className="flex space-x-3">
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger className="w-[140px]" data-testid="status-filter">
                    <SelectValue placeholder="All Status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="resume_reviewed">Resume Reviewed</SelectItem>
                    <SelectItem value="interview_scheduled">Interview Scheduled</SelectItem>
                    <SelectItem value="report_generated">Report Generated</SelectItem>
                    <SelectItem value="hired">Hired</SelectItem>
                    <SelectItem value="not_selected">Not Selected</SelectItem>
                  </SelectContent>
                </Select>
                <Select value={positionFilter} onValueChange={setPositionFilter}>
                  <SelectTrigger className="w-[160px]" data-testid="position-filter">
                    <SelectValue placeholder="All Positions" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Positions</SelectItem>
                    {jobs?.map((job: any) => (
                      <SelectItem key={job.id} value={job.id.toString()}>
                        {job.jobTitle}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardHeader>

          {/* Candidates Table */}
          <CardContent className="p-0">
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Candidate</TableHead>
                    <TableHead>Job Position</TableHead>
                    <TableHead>Skills</TableHead>
                    <TableHead>Experience</TableHead>
                    <TableHead>Match %</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {candidatesLoading ? (
                    <TableRow>
                      <TableCell colSpan={7} className="text-center py-8">
                        Loading candidates...
                      </TableCell>
                    </TableRow>
                  ) : filteredCandidates.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                        No candidates found
                      </TableCell>
                    </TableRow>
                  ) : (
                    filteredCandidates.map((candidate: any) => (
                      <TableRow key={candidate.id} className="hover:bg-muted/50" data-testid={`candidate-row-${candidate.id}`}>
                        <TableCell>
                          <div className="flex items-center space-x-3">
                            <Avatar className="h-10 w-10">
                              <AvatarFallback>
                                {candidate.candidateName?.charAt(0) || 'C'}
                              </AvatarFallback>
                            </Avatar>
                            <div>
                              <div className="font-medium text-foreground">{candidate.candidateName}</div>
                              <div className="text-sm text-muted-foreground">{candidate.email}</div>
                            </div>
                          </div>
                        </TableCell>
                        <TableCell className="text-foreground">
                          {getJobTitle(candidate.jobId)}
                        </TableCell>
                        <TableCell className="text-muted-foreground">
                          {candidate.candidateSkills?.join(', ') || 'No skills specified'}
                        </TableCell>
                        <TableCell className="text-muted-foreground">
                          {candidate.candidateExperience || 'Not specified'}
                        </TableCell>
                        <TableCell>
                          {candidate.matchPercentage ? (
                            <div className="flex items-center space-x-2">
                              <span className={`text-sm font-medium ${
                                candidate.matchPercentage >= 80 ? 'text-green-600' :
                                candidate.matchPercentage >= 60 ? 'text-yellow-600' :
                                'text-red-600'
                              }`}>
                                {Math.round(candidate.matchPercentage)}%
                              </span>
                              <Progress
                                value={candidate.matchPercentage}
                                className="w-16 h-2"
                              />
                            </div>
                          ) : (
                            <span className="text-muted-foreground">N/A</span>
                          )}
                        </TableCell>
                        <TableCell>
                          {getStatusBadge(candidate.status)}
                        </TableCell>
                        <TableCell>
                          <div className="flex space-x-2">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleEditCandidate(candidate)}
                              className="text-blue-600 hover:text-blue-700"
                              data-testid={`edit-candidate-${candidate.id}`}
                            >
                              <Edit className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteCandidate(candidate)}
                              className="text-red-600 hover:text-red-700"
                              data-testid={`delete-candidate-${candidate.id}`}
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </div>

            {/* Pagination */}
            {filteredCandidates.length > 0 && (
              <div className="px-6 py-4 border-t">
                <div className="flex items-center justify-between">
                  <div className="text-sm text-muted-foreground">
                    Showing <span className="font-medium">1</span> to{" "}
                    <span className="font-medium">{Math.min(filteredCandidates.length, 10)}</span> of{" "}
                    <span className="font-medium">{filteredCandidates.length}</span> results
                  </div>
                  <div className="flex space-x-2">
                    <Button variant="outline" size="sm" disabled>
                      Previous
                    </Button>
                    <Button variant="outline" size="sm" className="bg-primary text-primary-foreground">
                      1
                    </Button>
                    <Button variant="outline" size="sm" disabled>
                      Next
                    </Button>
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Add/Edit Candidate Modal */}
        {showAddModal && (
          <AddCandidateModal
            candidate={editingCandidate}
            onClose={handleCloseModal}
          />
        )}

        {/* Edit Candidate Dialog */}
        <Dialog open={showEditDialog} onOpenChange={setShowEditDialog}>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>Edit Candidate</DialogTitle>
              <DialogDescription>
                Update candidate status and information for {selectedCandidate?.candidateName}
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium">Status</label>
                <Select value={editFormData.status} onValueChange={(value) => setEditFormData(prev => ({...prev, status: value}))}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="resume_reviewed">Resume Reviewed</SelectItem>
                    <SelectItem value="interview_scheduled">Interview Scheduled</SelectItem>
                    <SelectItem value="report_generated">Report Generated</SelectItem>
                    <SelectItem value="hired">Hired</SelectItem>
                    <SelectItem value="not_selected">Not Selected</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {editFormData.status === 'interview_scheduled' && (
                <>
                  <div>
                    <label className="text-sm font-medium">Interview Link *</label>
                    <Input
                      value={editFormData.interviewLink}
                      onChange={(e) => setEditFormData(prev => ({...prev, interviewLink: e.target.value}))}
                      placeholder="https://zoom.us/j/..."
                      required
                    />
                  </div>
                  <div>
                    <label className="text-sm font-medium">Technical Person Email</label>
                    <Input
                      value={editFormData.technicalPersonEmail}
                      onChange={(e) => setEditFormData(prev => ({...prev, technicalPersonEmail: e.target.value}))}
                      placeholder="tech@company.com"
                      type="email"
                    />
                  </div>
                </>
              )}
            </div>

            <DialogFooter>
              <Button variant="outline" onClick={() => setShowEditDialog(false)}>
                Cancel
              </Button>
              <Button onClick={handleSaveEdit} disabled={updateCandidateMutation.isPending}>
                Save Changes
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        {/* Delete Candidate Dialog */}
        <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>Delete Candidate</DialogTitle>
              <DialogDescription>
                Are you sure you want to delete {selectedCandidate?.candidateName}? This action cannot be undone.
              </DialogDescription>
            </DialogHeader>

            <DialogFooter>
              <Button variant="outline" onClick={() => setShowDeleteDialog(false)}>
                Cancel
              </Button>
              <Button 
                variant="destructive" 
                onClick={handleConfirmDelete}
                disabled={deleteCandidateMutation.isPending}
              >
                Delete
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

client/src/pages/HRDashboard.tsx.content:
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Users, Briefcase, Clock, CheckSquare } from "lucide-react";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from "recharts";
import { Link } from "wouter";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";

export default function HRDashboard() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: stats, isLoading: statsLoading } = useQuery<{
    jobStats: { total: number; active: number };
    candidateStats: Array<{ status: string; count: number }>;
    pipelineData: Array<{ stage: string; count: number }>;
    chartData: Array<{ month: string; opened: number; filled: number }>;
  }>({
    queryKey: ["/api/dashboard/stats"],
    retry: false,
  });

  const { data: todos = [], isLoading: todosLoading } = useQuery<any[]>({
    queryKey: ["/api/todos"],
    retry: false,
  });

  if (isLoading || statsLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-muted-foreground">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  // Prepare chart data
  const chartData = stats?.chartData || [];
  const pipelineData = stats?.pipelineData || [];
  const candidateStats = stats?.candidateStats || [];

  return (
    <div className="min-h-screen bg-background">
      <div className="flex h-screen pt-16">
        {/* Main Content */}
        <div className="flex-1 p-6 pr-80 overflow-auto">
          <div className="max-w-7xl mx-auto space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold tracking-tight">HR Dashboard</h1>
                <p className="text-muted-foreground">Welcome back, {user?.name || user?.email}</p>
              </div>
            </div>

            {/* Quick Stats Cards - Clickable */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <Link href="/hr/jobs" data-testid="total-jobs-card">
                <Card className="cursor-pointer hover:shadow-lg transition-all duration-200 hover:-translate-y-1">
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Total Jobs</CardTitle>
                    <Briefcase className="h-4 w-4 text-muted-foreground" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold">{stats?.jobStats?.total || 0}</div>
                    <p className="text-xs text-muted-foreground">Active: {stats?.jobStats?.active || 0}</p>
                  </CardContent>
                </Card>
              </Link>

              <Link href="/hr/candidates" data-testid="total-candidates-card">
                <Card className="cursor-pointer hover:shadow-lg transition-all duration-200 hover:-translate-y-1">
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Total Candidates</CardTitle>
                    <Users className="h-4 w-4 text-muted-foreground" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold">
                      {candidateStats.reduce((sum, stat) => sum + Number(stat.count), 0)}
                    </div>
                    <p className="text-xs text-muted-foreground">In pipeline</p>
                  </CardContent>
                </Card>
              </Link>

              <Link href="/hr/notifications" data-testid="pending-tasks-card">
                <Card className="cursor-pointer hover:shadow-lg transition-all duration-200 hover:-translate-y-1">
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Pending Tasks</CardTitle>
                    <Clock className="h-4 w-4 text-muted-foreground" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold">
                      {todos.filter((todo: any) => !todo.isCompleted).length}
                    </div>
                    <p className="text-xs text-muted-foreground">To do items</p>
                  </CardContent>
                </Card>
              </Link>
            </div>

            {/* Charts Section */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Stacked Column Chart: Jobs Opened vs Filled */}
              <Card>
                <CardHeader>
                  <CardTitle>Jobs Opened vs Filled</CardTitle>
                  <CardDescription>Monthly comparison of job openings and fills</CardDescription>
                </CardHeader>
                <CardContent>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={chartData}>
                      <CartesianGrid strokeDasharray="3 3" className="stroke-muted" />
                      <XAxis dataKey="month" />
                      <YAxis />
                      <Tooltip 
                        contentStyle={{ 
                          backgroundColor: "hsl(var(--background))", 
                          border: "1px solid hsl(var(--border))",
                          borderRadius: "6px"
                        }}
                      />
                      <Bar dataKey="opened" fill="hsl(220, 70%, 50%)" name="Jobs Opened" />
                      <Bar dataKey="filled" fill="hsl(142, 76%, 36%)" name="Jobs Filled" />
                    </BarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>

              {/* Bar Chart: Candidate Status Distribution */}
              <Card>
                <CardHeader>
                  <CardTitle>Candidate Status Distribution</CardTitle>
                  <CardDescription>Candidates at each status in the hiring process</CardDescription>
                </CardHeader>
                <CardContent>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={pipelineData} layout="horizontal">
                      <CartesianGrid strokeDasharray="3 3" className="stroke-muted" />
                      <XAxis type="number" />
                      <YAxis dataKey="stage" type="category" width={120} />
                      <Tooltip 
                        contentStyle={{ 
                          backgroundColor: "hsl(var(--background))", 
                          border: "1px solid hsl(var(--border))",
                          borderRadius: "6px"
                        }}
                      />
                      <Bar dataKey="count" fill="hsl(var(--primary))" />
                    </BarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </div>

            {/* Pie Chart: Candidate Status Breakdown */}
            <Card>
              <CardHeader>
                <CardTitle>Candidate Status Breakdown</CardTitle>
                <CardDescription>Visual breakdown of all candidates by status</CardDescription>
              </CardHeader>
              <CardContent>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={candidateStats}
                      cx="50%"
                      cy="50%"
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="count"
                      label={({ status, count }) => `${status}: ${count}`}
                    >
                      {candidateStats.map((entry: any, index: number) => {
                        const colors = [
                          "hsl(220, 70%, 50%)", // Blue for Resume Reviewed
                          "hsl(32, 95%, 44%)",  // Orange for Interview Scheduled  
                          "hsl(271, 81%, 56%)", // Purple for Report Generated
                          "hsl(142, 76%, 36%)", // Green for Hired
                          "hsl(0, 84%, 60%)"    // Red for Not Selected
                        ];
                        return (
                          <Cell 
                            key={`cell-${index}`} 
                            fill={colors[index % colors.length]} 
                          />
                        );
                      })}
                    </Pie>
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: "hsl(var(--background))", 
                        border: "1px solid hsl(var(--border))",
                        borderRadius: "6px"
                      }}
                    />
                  </PieChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>
          </div>
        </div>

        {/* Right Side Panel - To-Do List */}
        <div className="fixed right-0 top-16 h-full w-80 bg-card border-l border-border p-6 overflow-auto">
          <div className="space-y-6">
            <div>
              <h2 className="text-xl font-semibold mb-4">To-Do List</h2>
              {todosLoading ? (
                <div className="flex items-center justify-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                </div>
              ) : todos.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <CheckSquare className="h-12 w-12 mx-auto mb-2 opacity-50" />
                  <p>No tasks found</p>
                  <p className="text-sm">You're all caught up!</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {todos.map((todo: any) => (
                    <div
                      key={todo.id}
                      className={`p-3 rounded-lg border ${
                        todo.isCompleted 
                          ? 'bg-muted/50 border-muted' 
                          : 'bg-background border-border'
                      }`}
                    >
                      <div className="flex items-start space-x-3">
                        <Checkbox
                          checked={todo.isCompleted}
                          className="mt-1"
                          data-testid={`todo-checkbox-${todo.id}`}
                        />
                        <div className="flex-1 min-w-0">
                          <p className={`text-sm ${
                            todo.isCompleted 
                              ? 'line-through text-muted-foreground' 
                              : 'text-foreground'
                          }`}>
                            {todo.task}
                          </p>
                          {todo.createdAt && (
                            <p className="text-xs text-muted-foreground mt-1">
                              {new Date(todo.createdAt).toLocaleDateString()}
                            </p>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/Jobs.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Plus, Search, Edit, Trash2 } from "lucide-react";
import AddJobModal from "@/components/AddJobModal";

export default function Jobs() {
  const { isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");

  const [showAddModal, setShowAddModal] = useState(false);
  const [editingJob, setEditingJob] = useState(null);

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: jobs = [], isLoading: jobsLoading, error } = useQuery<any[]>({
    queryKey: ["/api/jobs"],
    retry: false,
  });

  const deleteJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      await apiRequest(`/api/jobs/${jobId}`, { method: "DELETE" });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      toast({
        title: "Success",
        description: "Job deleted successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete job",
        variant: "destructive",
      });
    },
  });

  const handleDeleteJob = (jobId: number) => {
    if (window.confirm("Are you sure you want to delete this job?")) {
      deleteJobMutation.mutate(jobId);
    }
  };

  const handleEditJob = (job: any) => {
    setEditingJob(job);
    setShowAddModal(true);
  };

  const handleCloseModal = () => {
    setShowAddModal(false);
    setEditingJob(null);
  };

  const getStatusBadge = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'active':
        return <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">Active</Badge>;
      case 'closed':
        return <Badge className="bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">Closed</Badge>;
      case 'draft':
        return <Badge className="bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">Draft</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const filteredJobs = jobs?.filter((job: any) => {
    const matchesSearch = job.jobTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         job.jobDescription?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || job.jobStatus === statusFilter;

    return matchesSearch && matchesStatus;
  }) || [];

  if (isLoading || !isAuthenticated) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-foreground" data-testid="page-title">Jobs</h1>
            <p className="text-muted-foreground mt-1">Manage your job postings and requirements</p>
          </div>
          <Button onClick={() => setShowAddModal(true)} data-testid="add-job-button">
            <Plus className="mr-2 h-4 w-4" />
            Add Job
          </Button>
        </div>

        {/* Table Card */}
        <Card>
          {/* Table Controls */}
          <CardHeader className="border-b">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search jobs..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 max-w-sm"
                  data-testid="search-input"
                />
              </div>

              {/* Filters */}
              <div className="flex space-x-3">
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger className="w-[140px]" data-testid="status-filter">
                    <SelectValue placeholder="All Status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="active">Active</SelectItem>
                    <SelectItem value="closed">Closed</SelectItem>
                    <SelectItem value="draft">Draft</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardHeader>

          {/* Jobs Table */}
          <CardContent className="p-0">
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Job Title</TableHead>
                    <TableHead>ID</TableHead>
                    <TableHead>Added By</TableHead>
                    <TableHead>HR Handling</TableHead>
                    <TableHead>Experience</TableHead>
                    <TableHead>Positions</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {jobsLoading ? (
                    <TableRow>
                      <TableCell colSpan={8} className="text-center py-8">
                        Loading jobs...
                      </TableCell>
                    </TableRow>
                  ) : filteredJobs.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={8} className="text-center py-8 text-muted-foreground">
                        No jobs found
                      </TableCell>
                    </TableRow>
                  ) : (
                    filteredJobs.map((job: any) => (
                      <TableRow key={job.id} className="hover:bg-muted/50" data-testid={`job-row-${job.id}`}>
                        <TableCell>
                          <div>
                            <div className="font-medium text-foreground">{job.jobTitle}</div>
                            <div className="text-sm text-muted-foreground">
                              {job.skills?.join(', ') || 'No skills specified'}
                            </div>
                          </div>
                        </TableCell>
                        <TableCell className="text-muted-foreground">#{job.id}</TableCell>
                        <TableCell className="text-foreground">{job.addedByUserId || 'N/A'}</TableCell>
                        <TableCell className="text-foreground">{job.hrHandlingUserId || 'N/A'}</TableCell>
                        <TableCell className="text-muted-foreground">{job.experience || 'Not specified'}</TableCell>
                        <TableCell className="text-muted-foreground">{job.positionsCount || 1}</TableCell>
                        <TableCell>
                          {getStatusBadge(job.jobStatus)}
                        </TableCell>
                        <TableCell>
                          <div className="flex space-x-2">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleEditJob(job)}
                              data-testid={`edit-job-${job.id}`}
                            >
                              <Edit className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteJob(job.id)}
                              className="text-destructive hover:text-destructive"
                              data-testid={`delete-job-${job.id}`}
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </div>

            {/* Pagination */}
            {filteredJobs.length > 0 && (
              <div className="px-6 py-4 border-t">
                <div className="flex items-center justify-between">
                  <div className="text-sm text-muted-foreground">
                    Showing <span className="font-medium">1</span> to{" "}
                    <span className="font-medium">{Math.min(filteredJobs.length, 10)}</span> of{" "}
                    <span className="font-medium">{filteredJobs.length}</span> results
                  </div>
                  <div className="flex space-x-2">
                    <Button variant="outline" size="sm" disabled>
                      Previous
                    </Button>
                    <Button variant="outline" size="sm" className="bg-primary text-primary-foreground">
                      1
                    </Button>
                    <Button variant="outline" size="sm" disabled>
                      Next
                    </Button>
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Add/Edit Job Modal */}
        {showAddModal && (
          <AddJobModal
            job={editingJob}
            onClose={handleCloseModal}
          />
        )}
      </div>
    </div>
  );
}

client/src/pages/Login.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Brain } from "lucide-react";
import { Link } from "wouter";
import { apiRequest } from "@/lib/queryClient";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email"),
  password: z.string().min(1, "Password is required"),
  role: z.enum(["Super Admin", "Company Admin", "HR"], {
    required_error: "Please select a role",
  }),
  company: z.string().optional(),
}).refine((data) => {
  // Company is required for non-Super Admin roles
  if (data.role !== "Super Admin" && !data.company) {
    return false;
  }
  return true;
}, {
  message: "Company is required for Company Admin and HR roles",
  path: ["company"],
});

type LoginFormData = z.infer<typeof loginSchema>;

export default function Login() {
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const form = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
      role: undefined,
      company: "",
    },
  });

  const selectedRole = form.watch("role");
  const showCompanyField = selectedRole && selectedRole !== "Super Admin";

  const onSubmit = async (data: LoginFormData) => {
    setIsLoading(true);
    try {
      const response = await apiRequest("/api/auth/login", {
        method: "POST",
        body: data,
      });

      toast({
        title: "Login successful",
        description: "Welcome back!",
      });

      // Redirect based on role
      if (data.role === "Super Admin") {
        window.location.href = "/super-admin/dashboard";
      } else if (data.role === "Company Admin") {
        window.location.href = "/company-admin/dashboard";
      } else {
        window.location.href = "/hr/dashboard";
      }
    } catch (error: any) {
      toast({
        title: "Login failed",
        description: error.message || "Invalid credentials",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary/5 via-primary/10 to-background flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="w-16 h-16 bg-primary rounded-lg flex items-center justify-center mx-auto mb-4">
            <Brain className="text-primary-foreground" size={32} />
          </div>
          <CardTitle className="text-2xl">Smart Hiring</CardTitle>
          <CardDescription>Sign in to your account</CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            {/* Email */}
            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                placeholder="Enter your email"
                {...form.register("email")}
                data-testid="email-input"
              />
              {form.formState.errors.email && (
                <p className="text-sm text-destructive">
                  {form.formState.errors.email.message}
                </p>
              )}
            </div>

            {/* Role */}
            <div className="space-y-2">
              <Label htmlFor="role">Role</Label>
              <Select
                value={form.watch("role") || ""}
                onValueChange={(value) => form.setValue("role", value as any)}
              >
                <SelectTrigger data-testid="role-select">
                  <SelectValue placeholder="Select your role" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="Super Admin">Super Admin</SelectItem>
                  <SelectItem value="Company Admin">Company Admin</SelectItem>
                  <SelectItem value="HR">HR</SelectItem>
                </SelectContent>
              </Select>
              {form.formState.errors.role && (
                <p className="text-sm text-destructive">
                  {form.formState.errors.role.message}
                </p>
              )}
            </div>

            {/* Company (conditional) */}
            {showCompanyField && (
              <div className="space-y-2">
                <Label htmlFor="company">Company</Label>
                <Input
                  id="company"
                  placeholder="Enter your company name"
                  {...form.register("company")}
                  data-testid="company-input"
                />
                {form.formState.errors.company && (
                  <p className="text-sm text-destructive">
                    {form.formState.errors.company.message}
                  </p>
                )}
              </div>
            )}

            {/* Password */}
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                placeholder="Enter your password"
                {...form.register("password")}
                data-testid="password-input"
              />
              {form.formState.errors.password && (
                <p className="text-sm text-destructive">
                  {form.formState.errors.password.message}
                </p>
              )}
            </div>

            {/* Submit Button */}
            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading}
              data-testid="login-button"
            >
              {isLoading ? "Signing in..." : "Sign In"}
            </Button>
          </form>

          <div className="mt-6 text-center text-sm">
            <p className="text-muted-foreground">
              Don't have an account?{" "}
              <Link href="/signup">
                <span className="text-primary hover:underline cursor-pointer">
                  Sign up
                </span>
              </Link>
            </p>
            <p className="mt-2">
              <Link href="/">
                <span className="text-primary hover:underline cursor-pointer">
                  Back to Home
                </span>
              </Link>
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Notifications.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Bell, CheckCircle, Clock, X, Users, FileText, Calendar } from "lucide-react";

interface NotificationsProps {
  onClose?: () => void;
}

export default function Notifications({ onClose }: NotificationsProps) {
  const { isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: notifications = [], isLoading: notificationsLoading } = useQuery<any[]>({
    queryKey: ["/api/notifications"],
    retry: false,
  });

  const { data: todos = [], isLoading: todosLoading } = useQuery<any[]>({
    queryKey: ["/api/todos"],
    retry: false,
  });

  const markAllAsReadMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("PUT", "/api/notifications/read-all");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      toast({
        title: "Success",
        description: "All notifications marked as read",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to mark notifications as read",
        variant: "destructive",
      });
    },
  });

  const updateTodoMutation = useMutation({
    mutationFn: async ({ id, isCompleted }: { id: number; isCompleted: boolean }) => {
      await apiRequest("PUT", `/api/todos/${id}`, { isCompleted });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/todos"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update task",
        variant: "destructive",
      });
    },
  });

  const handleToggleTodo = (id: number, isCompleted: boolean) => {
    updateTodoMutation.mutate({ id, isCompleted: !isCompleted });
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return "Just now";
    if (diffInMinutes < 60) return `${diffInMinutes} minutes ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)} hours ago`;
    return `${Math.floor(diffInMinutes / 1440)} days ago`;
  };

  const getNotificationIcon = (message: string) => {
    if (message.toLowerCase().includes("candidate")) return <Users className="h-4 w-4" />;
    if (message.toLowerCase().includes("interview")) return <Calendar className="h-4 w-4" />;
    if (message.toLowerCase().includes("report")) return <FileText className="h-4 w-4" />;
    return <Bell className="h-4 w-4" />;
  };

  const unreadNotifications = notifications?.filter((n: any) => !n.readStatus) || [];
  const readNotifications = notifications?.filter((n: any) => n.readStatus) || [];
  const incompleteTodos = todos?.filter((t: any) => !t.isCompleted) || [];

  if (isLoading || !isAuthenticated) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="h-full flex">
      {/* To-Do Section (Left) */}
      <div className="flex-1 p-6 border-r border-border">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-foreground" data-testid="tasks-title">
            {incompleteTodos.length} tasks
          </h2>
          {onClose && (
            <Button variant="ghost" size="icon" onClick={onClose} data-testid="close-modal">
              <X className="h-4 w-4" />
            </Button>
          )}
        </div>

        <ScrollArea className="h-[600px] pr-4">
          <div className="space-y-4">
            {/* 360Â° Review Section */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-lg">360Â° Review</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                  <span className="text-sm text-foreground">Write reviews for</span>
                  <Button size="sm" data-testid="write-reviews-button">
                    Write reviews
                  </Button>
                </div>
                <div className="flex -space-x-1">
                  <Avatar className="w-8 h-8 border-2 border-background">
                    <AvatarFallback className="text-xs">JD</AvatarFallback>
                  </Avatar>
                  <Avatar className="w-8 h-8 border-2 border-background">
                    <AvatarFallback className="text-xs">SM</AvatarFallback>
                  </Avatar>
                  <Avatar className="w-8 h-8 border-2 border-background">
                    <AvatarFallback className="text-xs">AB</AvatarFallback>
                  </Avatar>
                  <div className="w-8 h-8 rounded-full bg-muted border-2 border-background flex items-center justify-center">
                    <span className="text-xs">+3</span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Sample Tasks */}
            <Card>
              <CardContent className="pt-4 space-y-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-start space-x-3">
                    <Checkbox className="mt-1" data-testid="direct-report-task" />
                    <div>
                      <span className="text-sm text-foreground">Turn on updates for your new direct report</span>
                      <div className="flex items-center mt-2">
                        <Avatar className="w-6 h-6 mr-2">
                          <AvatarFallback className="text-xs">FG</AvatarFallback>
                        </Avatar>
                        <span className="text-sm text-muted-foreground">Fletcher Guerrero</span>
                      </div>
                    </div>
                  </div>
                </div>

                <Separator />

                <div className="flex items-center justify-between">
                  <div className="flex items-start space-x-3">
                    <Checkbox className="mt-1" data-testid="goals-task" />
                    <span className="text-sm text-foreground">Update your 8 goals</span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Dynamic Tasks from API */}
            {todosLoading ? (
              <Card>
                <CardContent className="py-8 text-center">
                  <p className="text-sm text-muted-foreground">Loading tasks...</p>
                </CardContent>
              </Card>
            ) : incompleteTodos.length > 0 ? (
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Your Tasks</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  {incompleteTodos.map((todo: any) => (
                    <div key={todo.id} className="flex items-start space-x-3 p-3 bg-muted/50 rounded-lg">
                      <Checkbox
                        checked={todo.isCompleted}
                        onCheckedChange={() => handleToggleTodo(todo.id, todo.isCompleted)}
                        className="mt-1"
                        data-testid={`todo-${todo.id}`}
                      />
                      <span className={`text-sm ${todo.isCompleted ? 'line-through text-muted-foreground' : 'text-foreground'}`}>
                        {todo.task}
                      </span>
                    </div>
                  ))}
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="py-8 text-center">
                  <CheckCircle className="h-12 w-12 text-green-500 mx-auto mb-3" />
                  <h3 className="text-lg font-medium text-foreground mb-2">All caught up!</h3>
                  <p className="text-sm text-muted-foreground">No pending tasks at the moment.</p>
                </CardContent>
              </Card>
            )}
          </div>
        </ScrollArea>

        <div className="mt-6">
          <Button variant="ghost" size="sm" data-testid="view-more-tasks">
            View more tasks
          </Button>
        </div>
      </div>

      {/* Notifications Section (Right) */}
      <div className="w-80 p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-lg font-semibold text-foreground" data-testid="notifications-title">
            Notifications
          </h2>
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => markAllAsReadMutation.mutate()}
            disabled={unreadNotifications.length === 0}
            data-testid="mark-all-read"
          >
            Mark all as read
          </Button>
        </div>

        <ScrollArea className="h-[600px] pr-4">
          <div className="space-y-4">
            {notificationsLoading ? (
              <div className="text-center py-8">
                <p className="text-sm text-muted-foreground">Loading notifications...</p>
              </div>
            ) : notifications && notifications.length > 0 ? (
              <>
                {/* Unread Notifications */}
                {unreadNotifications.map((notification: any) => (
                  <div
                    key={notification.id}
                    className={`p-3 rounded-lg border-l-4 ${
                      notification.message.toLowerCase().includes('deadline') || notification.message.toLowerCase().includes('reminder')
                        ? 'bg-yellow-50 dark:bg-yellow-900/20 border-yellow-500'
                        : 'bg-primary/5 border-primary'
                    }`}
                    data-testid={`notification-${notification.id}`}
                  >
                    <div className="flex items-start space-x-2">
                      <div className="mt-1">
                        {getNotificationIcon(notification.message)}
                      </div>
                      <div className="flex-1">
                        <p className="text-sm font-medium text-foreground">
                          {notification.message}
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">
                          {formatTimestamp(notification.timestamp)}
                        </p>
                      </div>
                      {!notification.readStatus && (
                        <Badge variant="secondary" className="ml-2">
                          New
                        </Badge>
                      )}
                    </div>
                  </div>
                ))}

                {/* Read Notifications */}
                {readNotifications.slice(0, 5).map((notification: any) => (
                  <div
                    key={notification.id}
                    className="p-3 bg-muted/50 rounded-lg"
                    data-testid={`notification-${notification.id}`}
                  >
                    <div className="flex items-start space-x-2">
                      <div className="mt-1 text-muted-foreground">
                        {getNotificationIcon(notification.message)}
                      </div>
                      <div className="flex-1">
                        <p className="text-sm text-muted-foreground">
                          {notification.message}
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">
                          {formatTimestamp(notification.timestamp)}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </>
            ) : (
              <div className="text-center py-8">
                <Bell className="h-12 w-12 text-muted-foreground mx-auto mb-3" />
                <h3 className="text-lg font-medium text-foreground mb-2">No notifications</h3>
                <p className="text-sm text-muted-foreground">You're all caught up!</p>
              </div>
            )}
          </div>
        </ScrollArea>

        {notifications && notifications.length > 5 && (
          <div className="mt-6">
            <Button variant="ghost" size="sm" data-testid="see-more-notifications">
              See more notifications
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

